import React, {useEffect, useReducer} from 'react';
import './styles.css' ;
import {todoReducer} from './todoReducer';
import useForm from '../../hooks/useForm';

const init = () => {

  return JSON.parse(localStorage.getItem('todos')) || [];
};

const TodoApp = () => {

  const [todos, dispatch] = useReducer(todoReducer, [], init);


  const [{description}, handleInputChange, reset] = useForm({

    description:''
  });



  // Should jsonite the objecto to can storage
  useEffect(() => {

    localStorage.setItem('todos', JSON.stringify(todos))
  }, [todos])



  const handleDelete = (todoId) => {


    const delTodoAction = {

      type:'delete', 
      payload:todoId
    };


    dispatch(delTodoAction)


    console.log(todoId)
    //create action
    //dispatch

  };



  const handleSubmit = (e) => {
    //Esto evita que recargue la pagina una vez hecho el submit
    e.preventDefault();

    console.log('Trough submit now');


    if(description.trim().length <= 1){return}


    const newTodo = {

      id:new Date().getTime(), 
      desc:description, 
      done: false
    };


    const addTodoAction = {

      type:'add', 
      payload:newTodo
    };

    dispatch(addTodoAction);

    reset();
  };



  //<p className='text-center complete'>{++i}  {todo.desc}</p>

  return (
    <>
      <h1>TodoApp</h1> 

      <h4>Remaining Tasks: {todos.length} </h4>

      <div className='row'>

        <div className='col-7'>
          <ul className='list-group list-group-flush'>
            {
              todos.map((todo, i)=>(

                <li
                  key={todo.id}
                  className='list-group-item'
                >
                  <p className='text-center'>{++i}&emsp;{todo.desc}</p>

                  <button 
                    className='btn btn-danger'
                    onClick={()=>handleDelete(todo.id)}
                  >
                   x 
                  </button>
                </li>
              ))
            }
          </ul>
        </div>

        <div className='col-5'>

          <form onSubmit={handleSubmit}>
            <input 
              type='text'
              name= 'description'
              className='form-control'
              autoComplete='off'
              value={description}
              onChange={handleInputChange}
            />

            <button 
              className='btn btn-primary mt-1 btn-block'
              type='submit'
            >
             + 
            </button>
          </form>
        </div>
      </div>

    </>
  )}

export default TodoApp;
