import React, {useRef} from 'react';

const FocusScreen = () => {


  const ref = useRef();
  console.log(ref);


  const handleClick = () => {

    document.querySelector('input').select();

  };


  return (
    <>
      <h1>FocusScreen</h1> 
      <input
        className= 'form-control'
      />
      <button
        className= 'btn btn-outline-primary'
        onClick={handleClick}
      >
        Focus
      </button>
    </>
  )
}

export default FocusScreen;
