import React, {useReducer} from 'react';
import './styles.css' ;
import {todoReducer} from './todoReducer';

const initializerArg = [{

  id:new Date().getTime(), 
  desc:'Learn react', 
  done: false

}];

const TodoApp = () => {

  const [todos] = useReducer(todoReducer, initializerArg);
  console.log(todos);


  return (
    <>
      <h1>TodoApp</h1> 

      <small>Things to do: {todos.length} </small>

      <ul className='list-group list-group-flush'>
        {

          todos.map(todo=>(

            <li
              key={todo.id}
              className='list-group-item'o
            >
              {todos.desc}
            </li>
          ))

        }
      </ul>
    </>
  )}

export default TodoApp;
