import React, {useState} from 'react';
import AppRouter from './AppRouter';
import {UserContext} from './UserContext';

const MainApp = () => {
  const [user, setUser] = useState({
    id:123
  });
  return (
    <UserContext.Provider value={user}>
      <AppRouter/>
    </UserContext.Provider>
  )}

export default MainApp;
