const initalState = [{

  id:1, 
  todo:'buy bread', 
  done:false

}];

const todoReducer = (state=initalState, action) => {

  return state;

};

const newTodo = {

  id:1, 
  todo:'buy bread', 
  done:false

};

const agregarTodoAction = {
  type: 'agregar', 
  payload: newTodo
};

let todos = todoReducer(todos, agregarTodoAction);

console.log(todos);
