import React, {useReducer} from 'react';
import './styles.css' ;
import {todoReducer} from './todoReducer';
import useForm from '../../hooks/useForm';

const initializerArg = [{

  id:new Date().getTime(), 
  desc:'Learn react', 
  done: false

}];

const TodoApp = () => {

  const [todos, dispatch] = useReducer(todoReducer, initializerArg);

  const [{description}, handleInputChange] = useForm({

      description:''

  });
  console.log(description);

  const handleSubmit = (e) => {
    //Esto evita que recargue la pagina una vez hecho el submit
    e.preventDefault();

    console.log('Trough submit now');

    const newTodo = {

      id:new Date().getTime(), 
      desc:'New System', 
      done: false
    };

    const addTodoAction = {

      type:'add', 
      payload:newTodo

    };

    dispatch(addTodoAction);
  };

  //<p className='text-center complete'>{++i}  {todo.desc}</p>

  return (
    <>
      <h1>TodoApp</h1> 

      <h4>Remaining Tasks: {todos.length} </h4>

      <div className='row'>

        <div className='col-7'>
          <ul className='list-group list-group-flush'>
            {
              todos.map((todo, i)=>(

                <li
                  key={todo.id}
                  className='list-group-item'
                >
                  <p className='text-center'>{++i}  {todo.desc}</p>

                  <button className='btn btn-danger'>
                    Del
                  </button>
                </li>
              ))
            }
          </ul>
        </div>

        <div className='col-5'>
          <h4>Atomics Systems</h4> 

          <form onSubmit={handleSubmit}>
            <input 
              type='text'
              name= 'description'
              className='form-control'
              autoComplete='off'
              onChange={handleInputChange}
            />

            <button 
              className='btn btn-primary mt-1 btn-block'
              type='submit'
            >
              Add
            </button>
          </form>
        </div>
      </div>

    </>
  )}

export default TodoApp;
