import React, {useReducer} from 'react';
import './styles.css' ;
import {todoReducer} from './todoReducer';

const initializerArg = [{

  id:new Date().getTime(), 
  desc:'Learn react', 
  done: false

}];

const TodoApp = () => {

  const [todos] = useReducer(todoReducer, initializerArg);
  console.log(todos);

  //<p className='text-center complete'>{++i}  {todo.desc}</p>

  return (
    <>
      <h1>TodoApp</h1> 

      <h4>Remaining Tasks: {todos.length} </h4>

      <div className='row'>

        <div className='col-7'>
          <ul className='list-group list-group-flush'>
            {
              todos.map((todo, i)=>(

                <li
                  key={todo.id}
                  className='list-group-item'
                >
                  <p className='text-center complete'>{++i}  {todo.desc}</p>

                  <button className='btn btn-danger'>
                    Del
                  </button>
                </li>
              ))
            }
          </ul>
        </div>

        <div className='col-5'>
          Add
        </div>
      </div>

    </>
  )}

export default TodoApp;
