const initalState = [{

  id:1, 
  todo:'buy bread', 
  done:false

}];

const todoReducer = (state=initalState, action) => {

  if(action?.type === 'add'){

      return [...state, action.payload]

  }

  return state;

};

const newTodo = {

  id:1, 
  todo:'buy bread', 
  done:false

};

const agregarTodoAction = {
  type: 'add', 
  payload: newTodo
};

let todos = todoReducer(todos, agregarTodoAction);

console.log(todos);
