import React, {useEffect, useReducer} from 'react';
import './styles.css' ;
import {todoReducer} from './todoReducer';
import useForm from '../../hooks/useForm';
import TodoList from './TodoList';
import TodoAdd from './TodoAdd';

const init = () => {

  return JSON.parse(localStorage.getItem('todos')) || [];
};

const TodoApp = () => {

  const [todos, dispatch] = useReducer(todoReducer, [], init);





  // Should jsonite the objecto to can storage
  useEffect(() => {

    localStorage.setItem('todos', JSON.stringify(todos))
  }, [todos])



  const handleDelete = (todoId) => {


    const delTodoAction = {

      type:'delete', 
      payload:todoId
    };


    dispatch(delTodoAction)
  };



  const handleToggle = (todoId) => {
    dispatch({

      type:'toggle', 
      payload:todoId
    }) 
  }



  const handleAddTodo = (newTodo) => {

    dispatch({

      type:'add', 
      payload:newTodo
    });
  };


  const handleSubmit = (e) => {
    //Esto evita que recargue la pagina una vez hecho el submit
    e.preventDefault();

    console.log('Trough submit now');


    if(description.trim().length <= 1){return}


    const newTodo = {

      id:new Date().getTime(), 
      desc:description, 
      done: false
    };


    const addTodoAction = {

      type:'add', 
      payload:newTodo
    };

    dispatch(addTodoAction);

    reset();
  };



  //<p className='text-center complete'>{++i}  {todo.desc}</p>

  return (
    <>
      <h1>TodoApp</h1> 

      <h4>Remaining Tasks: {todos.length} </h4>

      <div className='row'>

        <div className='col-7'>
          <TodoList
            todos={todos}
            handleDelete={handleDelete}
            handleToggle={handleToggle}
          />
        </div>

        <div className='col-5'>

          <TodoAdd
          handleAddTodo={handleAddTodo}/>
        </div>
      </div>

    </>
  )}

export default TodoApp;
