import React from 'react';
import {Redirect, useParams} from 'react-router-dom';
import getHeroById from '../../selectors/getHeroById';

const HeroScreen = () => {


  const {heroeId} = useParams();
  const hero = getHeroById(heroeId);
  const {
  superhero, 
  publisher, 
  alter_ego, 
  first_appearance, 
  characters, 
  } = hero;


  if(!hero){
    return <Redirect to='/'/>}



  return (
    <>
      <h1>Hero</h1> 
    </>
  )}

export default HeroScreen;
