import React, {useEffect, useState}  from 'react';
import Modal from 'react-modal';
import DateTimePicker from 'react-datetime-picker';
import moment from 'moment';
import Swal from 'sweetalert2';
import {useDispatch, useSelector} from 'react-redux';
import {uiCloseModal} from '../../actions/ui';
import {eventAddNew, eventClearActiveEvent, eventUpdated} from '../../actions/events';

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};

// in this case '#' will be the index.html prop
Modal.setAppElement('#root')

const now = moment().minutes(0).seconds(0).add(1, 'hours');

const nowPlus1 = now.clone().add(1, 'hours');

const initEvent = {
  title:'', 
  notes:'', 
  start:now.toDate(), 
  end:nowPlus1.toDate(), 
};



export const CalendarModal = () => {


  //const [dateEnd, setDayEnd] = useState(now.add(1, 'hours').toDate());

  const [dateEnd, setDayEnd]        = useState(nowPlus1.toDate());
  const [dateStart, setDayStart]    = useState(now.toDate());
  const [titleValid, setTitleValid] = useState(true);
  const [formValues, setFormValues] = useState(initEvent);

  const {modalOpen} = useSelector(state => state.ui);
  const {activeEvent} = useSelector(state => state.cal);

  const dispatch = useDispatch();

  const {notes, title, start, end} = formValues;



  useEffect(() => {
    if(activeEvent){
      setFormValues(activeEvent)}
  }, [activeEvent, setFormValues])



  const handleInputChange = ({target}) => {
    setFormValues({
      ...formValues, 
      [target.name]: target.value
    })
  };

  const closeModal = () => {
    dispatch(uiCloseModal())
    dispatch(eventClearActiveEvent())
    setFormValues(initEvent)
  };


  const handleStartDateChange = (e) => {
    setDayStart(e)
    setFormValues({
      ...formValues, 
      start:e
    })
  };


  const handleEndDateChange = (e) => {
    setDayEnd(e) 
    setFormValues({
      ...formValues, 
      end:e
    })
  };



  const handleSubmitForm = (e) => {


    e.preventDefault();
    const momentStart = moment(start);
    const momentEnd= moment(end);

    if(momentStart.isSameOrAfter(momentEnd)){
      Swal.fire('Error', 'End date must be greater than start date', 'error') 
      return;
    }
    if(title.trim().length < 2){
      return setTitleValid(false);
    }

    if(activeEvent){

      dispatch(eventUpdated(formValues))}
    else {

      dispatch(eventAddNew({

        ...formValues, 
        id: new Date().getTime(), 
        user: {
          _id:'123', 
          name:'fernando'}
      }))
    }


    setTitleValid(true);
    closeModal()

    console.log(formValues)
  };



  return (
    <Modal
      isOpen={modalOpen}
      //onAfterOpen={afterOpenModal}
      onRequestClose={closeModal}
      style={customStyles}
      contentLabel="Example Modal"
      closeTimeoutMS={330}
      className="modal"
      overlayClassName='modal-fondo'
    >
      <h1> Nuevo evento </h1>
      <hr />
      <form 
        className="container"
        onSubmit={handleSubmitForm}
      >

        <div className="form-group">
          <label>Fecha y hora inicio</label>
          <DateTimePicker
            onChange={handleStartDateChange}
            value={dateStart}
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label>Fecha y hora fin</label>

          <DateTimePicker
            onChange={handleEndDateChange}
            value={dateEnd}
            className="form-control"
            minDate={dateStart}
          />
        </div>

        <hr />
        <div className="form-group">
          <label>Titulo y notas</label>
          <input 
            type="text" 
            className={`form-control ${!titleValid && 'is-invalid'}`}
            placeholder="Título del evento"
            name="title"
            autoComplete="off"

            value={title}
            onChange={handleInputChange}
          />
          <small id="emailHelp" className="form-text text-muted">Una descripción corta</small>
        </div>

        <div className="form-group">
          <textarea 
            type="text" 
            className="form-control"
            placeholder="Notas"
            rows="5"
            name="notes"

            value={notes}
            onChange={handleInputChange}
          ></textarea>
          <small id="emailHelp" className="form-text text-muted">Información adicional</small>
        </div>

        <button
          type="submit"
          className="btn btn-outline-primary btn-block"
        >
          <i className="far fa-save"></i>
          <span> Guardar</span>
        </button>

      </form>
    </Modal>
  )}
